  utm_medium:"article"
}); UTM Source Last ( utm_source_last ) JavaScript jstag.send({
  utm_source:"google_ads"
}); UTM Sources ( utm_sources ) JavaScript jstag.send({
  utm_source:"google_ads"
}); UTM Term Last ( utm_term_last ) JavaScript jstag.send({
  utm_term:"example"
}); UTM Terms ( utm_terms ) JavaScript jstag.send({
  utm_term:"example"
}); Campaign Hover ( ly_hover ) [needs update] JavaScript jstag.send({
